import React from 'react';
import { useSelector,useDispatch } from 'react-redux';
import SearchInput from './components/SearchInput';
import RepositoryCard from './components/RepositoryCard';
import UserCard from './components/UserCard';
import './App.css';
import {clearCacheStart } from './store/actions/searchActions';

function App() {
    const { loading, data, error, clearingCache } = useSelector(state => state);
    const dispatch = useDispatch();

    const handleClearCache = () => {
        dispatch(clearCacheStart());
    };


    return (
        <div className="app">
            <div className="header">
                <img src="data:image/png;base64," alt="GitHub" />
                <div className='header-text'>
                <h2>GitHub Searcher</h2>
                <p>Search users or repositories below</p>
                </div>
            </div>
            <div className="search-section">
                <SearchInput />
                <div>
                <button  className="dropdown"onClick={handleClearCache}>Clear Cache</button>
                </div>
            </div>
            {loading && <p>Loading...</p>}
            {error && <p> <b>Error: </b>{error}</p>}
            {clearingCache && <p>Clearing Cache ..... </p>}

            <div className="grid">
                {data.map(item => 
                    item.type ? 
                    <UserCard key={item.id} user={item} /> : 
                    <RepositoryCard key={item.id} repo={item} />
                )}
            </div>
            

        </div>
    );
}

export default App;
